            "IpVersion": "V4",
            "value": "{{public_ip}}"
        },
        {
            "IpAddressType": "PRIVATE_IP",
            "IpVersion": "V4",
            "value": "{{private_ip}}"
        }
    ],
    "nodeProperties": { //node properties are directly mapped to node candidate
        "price": "{{price}}", //e.g. "10.5"
        "providerId": "{{providerID}}", //e.g. "1"
        "cores": "{{cores}}", //e.g. "1"
        "cpuFrequency": "{{cpuFrequency}}", //e.g. "10.5"
        "ram": "{{ram}}", //e.g. "1"
        "disk": "{{disk}}", //e.g. "1.0"
        "fpga": "{{fpga}}", //e.g. "1"
        "gpu": "{{gpu}}", //e.g. "1"
        "operatingSystem": {
            "operatingSystemFamily": "{{OS_name}}",
            "operatingSystemArchitecture": "{{OS_arhitecture}}", 
            "operatingSystemVersion": "{{OS_version}}"
        },
        "geoLocation": {
            "city": "{{edge_city}}",
            "country": "{{edge_county}}",
            "latitude": "{{edge_latitude}}", //e.g. "52.237049"
            "longitude": "{{edge_latitude}}" //e.g. "21.017532"
        }
    },
    "port": "{{edge_port}}",
    "jobId": "{{jobId}}", // use "0" or "any" when device is NOT associated with ProActive job
    "systemArch": "{{edge_architecture}}", // MUST be "AMD", "ARMv8" OR "ARMv7"
    "scriptURL": "https://www.google.com",
    "jarURL": "{{jar_url}}" 
}

[
  // to retrive all node candidates it is sufficient to pass empty body. Later examples are provided to illustrate how to form the search quarry.

  // asking for IASS node type. In a case of searching for edge device it is to insert EDGE instead
  {
    "type": "NodeTypeRequirement",
    "nodeTypes": ["IAAS"]
  },
  // asking for nodes from specific cloud
  {
    "type": "AttributeRequirement",
    "requirementClass": "cloud",
    "requirementAttribute": "id",
    "requirementOperator": "EQ",
    "value": "{{cloud_name}}"
  },
  // asking for UBUNTU operating system
  {
    "type": "AttributeRequirement",
    "requirementClass": "image",
    "requirementAttribute": "operatingSystem.family",
    "requirementOperator": "IN",
    "value": "UBUNTU"
  },
  // asking for 22 version of OS
  {
    "type": "AttributeRequirement",
    "requirementClass": "image",
    "requirementAttribute": "name",
    "requirementOperator": "INC",
    "value": "22"
  },
  // asking for specific region
  {
    "type": "AttributeRequirement",
    "requirementClass": "location",
    "requirementAttribute": "name",
    "requirementOperator": "EQ",
    "value": "bgo"
  },
  // asking for 8GB RAM
  {
    "type": "AttributeRequirement",
    "requirementClass": "hardware",
    "requirementAttribute": "ram",
    "requirementOperator": "EQ",
    "value": "8192"
  },
  // asking for 4 cores
  {
    "type": "AttributeRequirement",
    "requirementClass": "hardware",
    "requirementAttribute": "cores",
    "requirementOperator": "EQ",
    "value": "4"
  },
  // asking specific hardware name
  {
    "type": "AttributeRequirement",
    "requirementClass": "hardware",
    "requirementAttribute": "name",
    "requirementOperator": "EQ",
    "value": "c5.xlarge"
  }
]

